import React, { useState, useEffect } from 'react';
import './App.css';

const App = () => {
  const [activeStage, setActiveStage] = useState(null);
  const [isProcessing, setIsProcessing] = useState(false);
  const [processStep, setProcessStep] = useState(0);
  const [materials, setMaterials] = useState({
    castorOil: { amount: 10, unit: 'ml', color: '#FFD700' },
    distilledWater: { amount: 40, unit: 'ml', color: '#87CEEB' },
    naoh: { amount: 5, unit: 'gm', color: '#FF6B6B' },
    glycerol: { amount: 10, unit: 'ml', color: '#98FB98' },
    nacl: { amount: 5, unit: 'gm', color: '#F0F8FF' },
    nahco3: { amount: 5, unit: 'gm', color: '#DDA0DD' }
  });

  const startProcess = () => {
    setIsProcessing(true);
    setProcessStep(1);
  };

  useEffect(() => {
    if (isProcessing && processStep < 5) {
      const timer = setTimeout(() => {
        setProcessStep(prev => prev + 1);
      }, 2000);
      return () => clearTimeout(timer);
    } else if (processStep >= 5) {
      setIsProcessing(false);
    }
  }, [isProcessing, processStep]);

  const stages = [
    { id: 1, name: 'Raw Material Storage', x: 50, y: 100 },
    { id: 2, name: 'Mixing Reactor', x: 300, y: 200 },
    { id: 3, name: 'Saponification', x: 550, y: 200 },
    { id: 4, name: 'Filtration', x: 800, y: 200 },
    { id: 5, name: 'Final Storage', x: 1050, y: 100 }
  ];

  return (
    <div className="factory-container">
      <header className="header">
        <h1>Liquid Soap Factory - Chemical Prototyping</h1>
        <p>Interactive Virtual Model for Saponification Process</p>
      </header>

      <div className="factory-layout">
        <svg width="1200" height="600" className="factory-svg">
          {/* Pipes */}
          <line x1="150" y1="150" x2="280" y2="200" stroke="#666" strokeWidth="8" />
          <line x1="350" y1="200" x2="530" y2="200" stroke="#666" strokeWidth="8" />
          <line x1="600" y1="200" x2="780" y2="200" stroke="#666" strokeWidth="8" />
          <line x1="850" y1="200" x2="1030" y2="150" stroke="#666" strokeWidth="8" />

          {/* Storage Tanks */}
          <g onClick={() => setActiveStage(1)}>
            <rect x="20" y="80" width="80" height="100" fill="#4CAF50" stroke="#333" strokeWidth="2" rx="5" />
            <text x="60" y="135" textAnchor="middle" fill="white" fontSize="12">Raw Materials</text>
          </g>

          {/* Mixing Reactor */}
          <g onClick={() => setActiveStage(2)}>
            <circle cx="300" cy="200" r="40" fill="#FF9800" stroke="#333" strokeWidth="2" />
            <text x="300" y="205" textAnchor="middle" fill="white" fontSize="12">Mixer</text>
          </g>

          {/* Saponification Reactor */}
          <g onClick={() => setActiveStage(3)}>
            <rect x="520" y="170" width="60" height="60" fill="#F44336" stroke="#333" strokeWidth="2" rx="10" />
            <text x="550" y="205" textAnchor="middle" fill="white" fontSize="10">Reactor</text>
          </g>

          {/* Filter */}
          <g onClick={() => setActiveStage(4)}>
            <polygon points="780,180 820,180 810,220 790,220" fill="#9C27B0" stroke="#333" strokeWidth="2" />
            <text x="800" y="205" textAnchor="middle" fill="white" fontSize="10">Filter</text>
          </g>

          {/* Final Storage */}
          <g onClick={() => setActiveStage(5)}>
            <rect x="1020" y="80" width="80" height="100" fill="#2196F3" stroke="#333" strokeWidth="2" rx="5" />
            <text x="1060" y="135" textAnchor="middle" fill="white" fontSize="12">Final Product</text>
          </g>

          {/* Flow indicators */}
          <polygon points="200,145 210,150 200,155" fill="#666" />
          <polygon points="450,195 460,200 450,205" fill="#666" />
          <polygon points="700,195 710,200 700,205" fill="#666" />
          <polygon points="950,145 960,150 950,155" fill="#666" />
        </svg>

        {/* Control Panel */}
        <div className="control-panel">
          <h3>Process Control</h3>
          <div className="materials-list">
            <h4>Raw Materials (ml/gm):</h4>
            <div>Castor Oil: {materials.castorOil}ml</div>
            <div>Distilled Water: {materials.distilledWater}ml</div>
            <div>NaOH: {materials.naoh}gm</div>
            <div>Glycerol: {materials.glycerol}ml</div>
            <div>NaCl: {materials.nacl}gm</div>
            <div>NaHCO₃: {materials.nahco3}gm</div>
          </div>

          {activeStage && (
            <div className="stage-info">
              <h4>Stage {activeStage}: {stages[activeStage - 1].name}</h4>
              {activeStage === 1 && (
                <div>
                  <p>Raw material storage tanks containing all ingredients for saponification process.</p>
                  <p>Temperature: 25°C | Pressure: 1 atm</p>
                </div>
              )}
              {activeStage === 2 && (
                <div>
                  <p>Mixing reactor where oils and alkali solution are combined.</p>
                  <p>Temperature: 60-80°C | Mixing Speed: 200 RPM</p>
                </div>
              )}
              {activeStage === 3 && (
                <div>
                  <p>Saponification reactor where chemical reaction occurs: Oil + KOH → Soap + Glycerin</p>
                  <p>Temperature: 80-100°C | Reaction Time: 2-4 hours</p>
                </div>
              )}
              {activeStage === 4 && (
                <div>
                  <p>Filtration system to remove impurities and excess materials.</p>
                  <p>Filter Type: Membrane | Pore Size: 0.1μm</p>
                </div>
              )}
              {activeStage === 5 && (
                <div>
                  <p>Final product storage with quality control testing.</p>
                  <p>pH: 9-10 | Viscosity: 1000-3000 cP</p>
