import React, { useState, useEffect } from 'react';
import './App.css';

const App = () => {
  const [activeSection, setActiveSection] = useState('virtual-model');
  const [activeStage, setActiveStage] = useState(null);
  const [isProcessing, setIsProcessing] = useState(false);
  const [processStep, setProcessStep] = useState(0);
  const [materials] = useState({
    castorOil: { name: 'Castor Oil', color: '#FFD700' },
    distilledWater: { name: 'Distilled Water', color: '#87CEEB' },
    naoh: { name: 'Sodium Hydroxide', color: '#FF6B6B' },
    glycerol: { name: 'Glycerol', color: '#98FB98' },
    nacl: { name: 'Sodium Chloride', color: '#F0F8FF' },
    nahco3: { name: 'Sodium Bicarbonate', color: '#DDA0DD' }
  });

  const sections = [
    { id: 'virtual-model', name: 'Virtual Model', icon: '🏭' },
    { id: 'process-flow', name: 'Process Flow', icon: '⚗️' },
    { id: 'materials', name: 'Raw Materials', icon: '🧪' },
    { id: 'equipment', name: 'Equipment', icon: '⚙️' },
    { id: 'quality', name: 'Quality Control', icon: '✅' },
    { id: 'safety', name: 'Safety', icon: '🛡️' }
  ];

  const startProcess = () => {
    setIsProcessing(true);
    setProcessStep(1);
  };

  const getIngredientFunction = (ingredient) => {
    const functions = {
      castorOil: 'Primary fat source for saponification reaction',
      distilledWater: 'Solvent for dissolving alkali and dilution',
      naoh: 'Strong alkali catalyst for saponification process',
      glycerol: 'Moisturizing agent and skin conditioning',
      nacl: 'Thickening agent and viscosity modifier',
      nahco3: 'pH buffer and foam stabilizer'
    };
    return functions[ingredient] || 'Chemical ingredient';
  };

  useEffect(() => {
    if (isProcessing && processStep < 5) {
      const timer = setTimeout(() => {
        setProcessStep(prev => prev + 1);
      }, 2000);
      return () => clearTimeout(timer);
    } else if (processStep >= 5) {
      setIsProcessing(false);
    }
  }, [isProcessing, processStep]);

  const renderContent = () => {
    switch(activeSection) {
      case 'virtual-model':
        return renderVirtualModel();
      case 'process-flow':
        return <div className="content-section"><h2>Process Flow Diagram</h2><p>Detailed process flow documentation...</p></div>;
      case 'materials':
        return <div className="content-section"><h2>Raw Materials Specification</h2><p>Complete material specifications...</p></div>;
      case 'equipment':
        return <div className="content-section"><h2>Equipment Details</h2><p>Technical equipment specifications...</p></div>;
      case 'quality':
        return <div className="content-section"><h2>Quality Control Parameters</h2><p>Quality assurance protocols...</p></div>;
      case 'safety':
        return <div className="content-section"><h2>Safety Protocols</h2><p>Safety guidelines and procedures...</p></div>;
      default:
        return renderVirtualModel();
    }
  };

  const renderVirtualModel = () => (
    <div className="factory-box">
      <div className="factory-header">
        <h2>Virtual Factory Model</h2>
        <button className="start-btn" onClick={startProcess} disabled={isProcessing}>
          {isProcessing ? 'Processing...' : 'Start'}
        </button>
      </div>

      <div className="factory-layout">
        <div className="materials-row">
          {Object.entries(materials).map(([key, material]) => (
            <div key={key} className="material-tank" onClick={() => setActiveStage(key)}>
              <div className="tank" style={{backgroundColor: material.color}}>
                <div className="tank-top"></div>
              </div>
              <div className="tank-label">{material.name}</div>
            </div>
          ))}
        </div>

        <div className="piping-system">
          <div className="pipe-section">
            <div className="pipe-horizontal pipe-1">
              <div className={`flow ${processStep >= 1 ? 'active' : ''}`}></div>
              <div className="pipe-label">Feed Line</div>
            </div>
            <div className="valve valve-1">V-001</div>
          </div>

          <div className="pipe-section">
            <div className="pipe-horizontal pipe-2">
              <div className={`flow ${processStep >= 2 ? 'active' : ''}`}></div>
              <div className="pipe-label">Mix Line</div>
            </div>
            <div className="valve valve-2">V-002</div>
          </div>

          <div className="pipe-section">
            <div className="pipe-horizontal pipe-3">
              <div className={`flow ${processStep >= 3 ? 'active' : ''}`}></div>
              <div className="pipe-label">React Line</div>
            </div>
            <div className="valve valve-3">V-003</div>
          </div>

          <div className="pipe-section">
            <div className="pipe-horizontal pipe-4">
              <div className={`flow ${processStep >= 4 ? 'active' : ''}`}></div>
              <div className="pipe-label">Filter Line</div>
            </div>
            <div className="valve valve-4">V-004</div>
          </div>
        </div>

        <div className="equipment-row">
          <div className={`equipment mixer ${processStep >= 2 ? 'active' : ''}`} onClick={() => setActiveStage(2)}>
            <div className="connection-in"></div>
            <div className="eq-body" style={{backgroundColor: '#FF9800'}}>
              {processStep >= 2 && <div className="spinner"></div>}
            </div>
            <div className="connection-out"></div>
            <div className="eq-label">Mixer R-101</div>
          </div>

          <div className={`equipment reactor ${processStep >= 3 ? 'active' : ''}`} onClick={() => setActiveStage(3)}>
            <div className="connection-in"></div>
            <div className="eq-body" style={{backgroundColor: '#F44336'}}>
              {processStep >= 3 && <div className="bubbles"></div>}
            </div>
            <div className="connection-out"></div>
            <div className="eq-label">Reactor R-102</div>
          </div>

          <div className={`equipment filter ${processStep >= 4 ? 'active' : ''}`} onClick={() => setActiveStage(4)}>
            <div className="connection-in"></div>
            <div className="eq-body" style={{backgroundColor: '#9C27B0'}}></div>
            <div className="connection-out"></div>
            <div className="eq-label">Filter F-101</div>
          </div>

          <div className={`equipment storage ${processStep >= 5 ? 'active' : ''}`} onClick={() => setActiveStage(5)}>
            <div className="connection-in"></div>
            <div className="eq-body" style={{backgroundColor: '#2196F3'}}>
              {processStep >= 5 && <div className="liquid"></div>}
            </div>
            <div className="eq-label">Storage T-101</div>
          </div>
        </div>

        <div className="status-row">
          <div className={`status ${isProcessing ? 'processing' : 'idle'}`}>
            Status: {isProcessing ? `Step ${processStep}/5` : 'Ready'}
          </div>
          {activeStage && (
            <div className="info">
              {materials[activeStage] ? materials[activeStage].name : 'Equipment Selected'}
            </div>
          )}
        </div>
      </div>
    </div>
  );

  return (
    <div className="app-container">
      <header className="professional-header">
        <div className="header-content">
          <div className="logo-section">
            <div className="logo">🏭</div>
            <div className="company-info">
              <h1>ChemProto Industries</h1>
              <p>Liquid Soap Manufacturing - Virtual Prototyping System</p>
            </div>
          </div>
          <div className="header-controls">
            <div className="status-indicator">
              <span className={`status-dot ${isProcessing ? 'processing' : 'idle'}`}></span>
              <span>{isProcessing ? 'Processing' : 'Ready'}</span>
            </div>
          </div>
        </div>
      </header>

      <div className="main-layout">
        <aside className="sidebar">
          <nav className="sidebar-nav">
            <h3>Navigation</h3>
            {sections.map(section => (
              <button 
                key={section.id}
                className={`nav-item ${activeSection === section.id ? 'active' : ''}`}
                onClick={() => setActiveSection(section.id)}
              >
                <span className="nav-icon">{section.icon}</span>
                <span className="nav-text">{section.name}</span>
              </button>
            ))}
          </nav>

          <div className="sidebar-info">
            <h4>System Info</h4>
            <div className="info-item">
              <span>Version:</span>
              <span>v2.1.0</span>
            </div>
            <div className="info-item">
              <span>Status:</span>
              <span className="status-online">Online</span>
            </div>
            <div className="info-item">
              <span>Last Update:</span>
              <span>2024-01-15</span>
            </div>
          </div>
        </aside>

        <main className="main-content">
          <div className="content-header">
            <h2>{sections.find(s => s.id === activeSection)?.name}</h2>
            <div className="breadcrumb">
              <span>Home</span> / <span>{sections.find(s => s.id === activeSection)?.name}</span>
            </div>
          </div>

          {renderContent()}
        </main>
      </div>
    </div>
  );
};

export default App;
