import React, { useState, useEffect } from 'react';
import './App.css';

const App = () => {
  const [activeStage, setActiveStage] = useState(null);
  const [isProcessing, setIsProcessing] = useState(false);
  const [processStep, setProcessStep] = useState(0);
  const [materials] = useState({
    castorOil: { name: 'Castor Oil', color: '#FFD700' },
    distilledWater: { name: 'Distilled Water', color: '#87CEEB' },
    naoh: { name: 'Sodium Hydroxide', color: '#FF6B6B' },
    glycerol: { name: 'Glycerol', color: '#98FB98' },
    nacl: { name: 'Sodium Chloride', color: '#F0F8FF' },
    nahco3: { name: 'Sodium Bicarbonate', color: '#DDA0DD' }
  });

  const startProcess = () => {
    setIsProcessing(true);
    setProcessStep(1);
  };

  const getIngredientFunction = (ingredient) => {
    const functions = {
      castorOil: 'Primary fat source for saponification reaction',
      distilledWater: 'Solvent for dissolving alkali and dilution',
      naoh: 'Strong alkali catalyst for saponification process',
      glycerol: 'Moisturizing agent and skin conditioning',
      nacl: 'Thickening agent and viscosity modifier',
      nahco3: 'pH buffer and foam stabilizer'
    };
    return functions[ingredient] || 'Chemical ingredient';
  };

  const getIngredientProperties = (ingredient) => {
    const properties = {
      castorOil: 'Viscous liquid, golden color, mild odor',
      distilledWater: 'Clear, colorless, odorless liquid',
      naoh: 'White crystalline solid, highly caustic',
      glycerol: 'Clear, viscous liquid, sweet taste',
      nacl: 'White crystalline solid, soluble in water',
      nahco3: 'White powder, mild alkaline properties'
    };
    return properties[ingredient] || 'Standard chemical properties';
  };

  useEffect(() => {
    if (isProcessing && processStep < 5) {
      const timer = setTimeout(() => {
        setProcessStep(prev => prev + 1);
      }, 2000);
      return () => clearTimeout(timer);
    } else if (processStep >= 5) {
      setIsProcessing(false);
    }
  }, [isProcessing, processStep]);

  const stages = [
    { id: 1, name: 'Raw Material Storage', x: 80, y: 120 },
    { id: 2, name: 'Mixing Reactor', x: 250, y: 180 },
    { id: 3, name: 'Saponification Reactor', x: 460, y: 180 },
    { id: 4, name: 'Filtration System', x: 650, y: 180 },
    { id: 5, name: 'Final Product Storage', x: 860, y: 120 }
  ];

  return (
    <div className="factory-container">
      <header className="header">
        <h1>Liquid Soap Factory - Chemical Prototyping</h1>
        <p>Interactive Virtual Model for Saponification Process</p>
      </header>

      <div className="factory-layout">
        <div className="ingredients-panel">
          <h3>Raw Material Storage</h3>
          <div className="storage-containers">
            {Object.entries(materials).map(([key, material]) => (
              <div key={key} className="storage-tank" onClick={() => setActiveStage(key)}>
                <div className="tank-3d" style={{backgroundColor: material.color}}>
                  <div className="tank-top"></div>
                  <div className="tank-body"></div>
                  <div className="tank-label">{material.name}</div>
                </div>
              </div>
            ))}
          </div>
          <button className="start-process-btn" onClick={startProcess} disabled={isProcessing}>
            {isProcessing ? 'Processing...' : 'Start Production'}
          </button>
        </div>

        <div className="factory-3d">
          <div className="factory-floor">
            <div className="equipment-row">

              {/* 3D Pipes */}
              <div className={`pipe-3d ${processStep >= 1 ? 'active' : ''}`}></div>

              {/* Mixing Reactor 3D */}
              <div className={`equipment-3d mixer ${activeStage === 2 ? 'active' : ''} ${processStep >= 2 ? 'processing' : ''}`} onClick={() => setActiveStage(2)}>
                <div className="equipment-body" style={{backgroundColor: '#FF9800'}}>
                  <div className="equipment-top"></div>
                  {processStep >= 2 && <div className="mixer-blade rotating"></div>}
                </div>
                <div className="equipment-label">Mixer</div>
              </div>

              <div className={`pipe-3d ${processStep >= 2 ? 'active' : ''}`}></div>

              {/* Saponification Reactor 3D */}
              <div className={`equipment-3d reactor ${activeStage === 3 ? 'active' : ''} ${processStep >= 3 ? 'processing' : ''}`} onClick={() => setActiveStage(3)}>
                <div className="equipment-body" style={{backgroundColor: '#F44336'}}>
                  <div className="equipment-top"></div>
                  {processStep >= 3 && <div className="bubbles"><span></span><span></span><span></span></div>}
                </div>
                <div className="equipment-label">Reactor</div>
              </div>

              <div className={`pipe-3d ${processStep >= 3 ? 'active' : ''}`}></div>

              {/* Filter 3D */}
              <div className={`equipment-3d filter ${activeStage === 4 ? 'active' : ''} ${processStep >= 4 ? 'processing' : ''}`} onClick={() => setActiveStage(4)}>
                <div className="equipment-body" style={{backgroundColor: '#9C27B0'}}>
                  <div className="equipment-top"></div>
                  <div className="filter-mesh"></div>
                </div>
                <div className="equipment-label">Filter</div>
              </div>

              <div className={`pipe-3d ${processStep >= 4 ? 'active' : ''}`}></div>

              {/* Final Storage 3D */}
              <div className={`equipment-3d storage ${activeStage === 5 ? 'active' : ''} ${processStep >= 5 ? 'processing' : ''}`} onClick={() => setActiveStage(5)}>
                <div className="equipment-body" style={{backgroundColor: '#2196F3'}}>
                  <div className="equipment-top"></div>
                  {processStep >= 5 && <div className="liquid-level"></div>}
                </div>
                <div className="equipment-label">Final Storage</div>
              </div>
            </div>
          </div>
        </div>



        {/* Control Panel */}
        <div className="control-panel">
          <h3>Process Control</h3>
          <div className="process-status">
            <h4>Current Status:</h4>
            <div className={`status-indicator ${isProcessing ? 'processing' : 'idle'}`}>
              {isProcessing ? `Step ${processStep}/5 - Processing` : 'Ready to Start'}
            </div>
          </div>

          <div className="process-details">
            <h4>Active Materials:</h4>
            <div className="active-materials">
              {Object.entries(materials).map(([key, material]) => (
                <div key={key} className={`material-indicator ${activeStage === key ? 'active' : ''}`}>
                  <div className="material-dot" style={{backgroundColor: material.color}}></div>
                  <span>{material.name}</span>
                </div>
              ))}
            </div>
          </div>

          {activeStage && (
            <div className="stage-info">
              <h4>Stage {activeStage}: {stages[activeStage - 1].name}</h4>
              {(activeStage === 'castorOil' || activeStage === 'distilledWater' || activeStage === 'naoh' || activeStage === 'glycerol' || activeStage === 'nacl' || activeStage === 'nahco3') && (
                <div>
                  <p><strong>Material:</strong> {materials[activeStage]?.name}</p>
                  <p><strong>Function:</strong> {getIngredientFunction(activeStage)}</p>
                  <p><strong>Storage:</strong> Individual sealed container with controlled environment</p>
                  <p><strong>Properties:</strong> {getIngredientProperties(activeStage)}</p>
                </div>
              )}
              {activeStage === 2 && (
                <div>
                  <p><strong>Function:</strong> Homogeneous mixing of oils with alkali solution</p>
                  <p><strong>Process:</strong> Castor oil is slowly added to NaOH solution while maintaining constant agitation</p>
                  <p><strong>Equipment:</strong> High-speed mechanical stirrer with temperature control</p>
                  <p><strong>Conditions:</strong> Temperature: 60-80°C | Mixing Speed: 200 RPM | Duration: 30 min</p>
                  <p><strong>Critical Parameters:</strong> Maintain temperature to prevent oil degradation</p>
                </div>
              )}
              {activeStage === 3 && (
                <div>
                  <p><strong>Function:</strong> Saponification reaction - conversion of fats to soap</p>
                  <p><strong>Chemical Reaction:</strong> C₃H₅(OCOR)₃ + 3NaOH → 3RCOONa + C₃H₅(OH)₃</p>
                  <p><strong>Process:</strong> Heated reaction vessel with continuous stirring for complete conversion</p>
                  <p><strong>Conditions:</strong> Temperature: 80-100°C | Reaction Time: 2-4 hours | pH: 12-13</p>
                  <p><strong>Monitoring:</strong> Temperature, pH, and saponification value checked hourly</p>
                </div>
              )}
              {activeStage === 4 && (
                <div>
                  <p><strong>Function:</strong> Removal of impurities, unreacted materials, and excess alkali</p>
                  <p><strong>Process:</strong> Multi-stage filtration with activated carbon and membrane filters</p>
                  <p><strong>Equipment:</strong> Pressure filtration system with 0.1μm membrane</p>
                  <p><strong>Conditions:</strong> Pressure: 2-3 bar | Flow Rate: 50 L/hr | Temperature: 40°C</p>
                  <p><strong>Quality Check:</strong> Clarity, color, and particle count analysis</p>
                </div>
              )}
              {activeStage === 5 && (
                <div>
                  <p><strong>Function:</strong> Final product storage with quality assurance testing</p>
                  <p><strong>Quality Parameters:</strong></p>
                  <ul>
                    <li>pH: 9-10 (skin-friendly range)</li>
                    <li>Viscosity: 1000-3000 cP</li>
                    <li>Total Fatty Matter: &gt;15%</li>
                    <li>Free Alkali: &lt;0.1%</li>
                    <li>Moisture Content: &lt;70%</li>
                  </ul>
                  <p><strong>Storage:</strong> Stainless steel tanks with nitrogen blanketing</p>
                  <p><strong>Shelf Life:</strong> 24 months under proper storage conditions</p>
                </div>
              )}
            </div>
          )}
        </div>
      </div>

      <div className="process-info">
        <div className="objective">
          <h3>Objective</h3>
          <p>Prepare liquid soap through saponification where fats/oils react with potassium hydroxide (KOH) to form soap and glycerin. Understanding chemical reactions, ingredient roles, and proper formulation for high-quality, stable, skin-friendly liquid soap.</p>
        </div>

        <div className="chemical-equation">
          <h3>Chemical Reaction</h3>
          <p>Fat/Oil + KOH → Soap + Glycerin</p>
          <p>C₃H₅(OCOR)₃ + 3KOH → 3RCOOK + C₃H₅(OH)₃</p>
        </div>
      </div>
    </div>
  );
};

export default App;
